import { Component } from "react";
import { ContainerApp } from "./App.styled";
import TodoList from "./TodoList";
// import Dropdown from "./Dropdown";
// import ColorPicker from "./ColorPicker";
// import Counter from "./Counter";
// import { colorPeckerOption } from "./arrayColorPecker";

export class App extends Component {
  state = {
    todo: [
      { id: "id-1", text: "Todo item one", completed: false },
      { id: "id-2", text: "Todo item two", completed: true },
      { id: "id-3", text: "Todo item three", completed: true },
      { id: "id-4", text: "Todo item five", completed: false },
    ],
  };

  render() {
    const { todo } = this.state;
    return (
      <ContainerApp>
        <TodoList todo={todo} />
      </ContainerApp>
    );
  }
}
